mapper.select:
/** [SIGN].[ContrcMapper.selectContrcList] */
SELECT A.CONTRACT_SEQ ,A.CONTRACT_NO ,A.FILE_SEQ ,A.USER_NM ,A.CELL_NO ,A.EMAIL ,A.SIGN_DUE_SDATE ,A.SIGN_DUE_EDATE ,A.IN_SIGN_DATE ,A.USE_YN ,A.REG_DATE ,B.FORM_NM ,B.FILE_PATH FROM contract A LEFT join file B on A.FILE_SEQ = B.FILE_SEQ and B.USE_YN = 'Y'
ORDER BY ${orderBy}
LIMIT #{length} OFFSET #{start}

/** [SIGN].[ContrcMapper.selectCompleteContr] */
select *
from file
WHERE CONTRACT_NO = #{CONTRACT_NO}
  and USE_YN = 'Y'

mapper.insert:
 /** [SIGN].[ContrcMapper.insertContrcReg] */
 insert into contract
 ( CONTRACT_NO
 , FILE_SEQ
 , USER_NM
 , CELL_NO
 , EMAIL
 , SIGN_DUE_SDATE
 , SIGN_DUE_EDATE
 , REG_ID
 , REG_DATE
 , MOD_ID
 , MOD_DATE
) values ( #{contractNo}
        , #{fileSeq}
        , #{userNm}
        , #{cellNo}
        , #{email}
        , #{signDueSdate}
        , #{signDueEdate}
        , #{regId}
        , current_timestamp() 
        , #{modId}
        , current_timestamp() 
        )

        /** [SIGN].[ContrcMapper.updateContrcReg] */
        update contract 
		set  USE_YN = 'N'
		    , MOD_ID =  #{modId}
		    , MOD_DATE = current_timestamp() 
		where CONTRACT_NO = #{contractNo}
