mapper.select:
/** [SIGN].[ContrcMapper.selectContrcList] */
SELECT A.CONTRC_SEQ ,A.CONTRC_NO ,A.FILE_SEQ ,A.PIN_NO ,A.USER_NM ,A.CELL_NO ,A.EMAIL ,A.SIGN_DUE_SDATE ,A.SIGN_DUE_EDATE ,A.IN_SIGN_DATE ,A.USE_YN ,A.REG_DATE ,B.FORM_NM ,B.FILE_PATH FROM contrc_mgmt A LEFT join file_mgmt B on A.FILE_SEQ = B.FILE_SEQ and B.USE_YN = 'Y'
ORDER BY ${orderBy}
LIMIT #{length} OFFSET #{start}

/** [SIGN].[ContrcMapper.selectCompleteContr] */
select *
from file_mgmt
WHERE CONTRC_NO = #{CONTRC_NO}
  and USE_YN = 'Y'

mapper.insert:
 /** [SIGN].[ContrcMapper.insertContrcReg] */
 insert into contrc_mgmt
 ( CONTRC_NO
 , FILE_SEQ
 , USER_NM
 , CELL_NO
 , EMAIL
 , SIGN_DUE_SDATE
 , SIGN_DUE_EDATE
 , REG_ID
 , REG_DATE
 , MOD_ID
 , MOD_DATE
) values ( #{contrcNo}
        , #{fileSeq}
        , #{userNm}
        , #{cellNo}
        , #{email}
        , #{signDueSdate}
        , #{signDueEdate}
        , #{regId}
        , current_timestamp() 
        , #{modId}
        , current_timestamp() 
        )

        /** [SIGN].[ContrcMapper.updateContrcReg] */
        update contrc_mgmt 
		set  USE_YN = 'N'
		    , MOD_ID =  #{modId}
		    , MOD_DATE = current_timestamp() 
		where CONTRC_NO = #{contrcNo}

mapper.update:
/** [SIGN].[ContrcMapper.updateContrcPin] */
update contrc_mgmt
set PIN_NO = #{PIN_NO}
  , MOD_ID = #{MOD_ID}
  , MOD_DATE = current_timestamp() 
where CONTRC_NO = #{CONTRC_NO}
