mapper.select:
/** [SIGN].[FormMapper.selectFileList] */
select B.* from ( SELECT A.*
,(@row_number := @row_number + 1) RN
FROM (SELECT FILE_SEQ ,FILE_TP ,CONTRC_NO ,FORM_NM ,ORG_FILE_NM ,SAV_FILE_NM ,FILE_PATH ,USE_YN ,REG_DATE ,REG_ID ,MOD_DATE ,MOD_ID FROM file_mgmt
,(SELECT @row_number := 0) AA
WHERE FORM_NM LIKE CONCAT('%',#{searchWord},'%')
AND FILE_TYPE = #{FILE_TYPE}
ORDER BY FILE_SEQ desc ) A ) B
WHERE B.RN >= #{pagingVO.start} AND B.RN <= #{pagingVO.end}

select *
from file_mgmt
where CONTRC_NO = #{contrcNo}
  and FILE_TP = #{fileType}
